import{_ as s,c as n,o as e,ah as l}from"./chunks/framework.BrJYEL96.js";const b=JSON.parse('{"title":"Lnmp之安装laravel应用","description":"","frontmatter":{},"headers":[],"relativePath":"backend/PHP/Lnmp/lnmp-laravel.md","filePath":"backend/PHP/Lnmp/lnmp-laravel.md","lastUpdated":1736052165000}'),o={name:"backend/PHP/Lnmp/lnmp-laravel.md"};function p(r,a,c,t,i,d){return e(),n("div",null,a[0]||(a[0]=[l(`<h1 id="lnmp之安装laravel应用" tabindex="-1">Lnmp之安装laravel应用 <a class="header-anchor" href="#lnmp之安装laravel应用" aria-label="Permalink to &quot;Lnmp之安装laravel应用&quot;">​</a></h1><p><a href="https://blog.csdn.net/bhjgyfyki/article/details/111867284" target="_blank" rel="noreferrer">原文</a> <a href="https://lnmp.org/install.html" target="_blank" rel="noreferrer">官网</a></p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">wget</span><span style="color:#98C379;"> http://soft.vpser.net/lnmp/lnmp2.0.tar.gz</span><span style="color:#D19A66;"> -O</span><span style="color:#98C379;"> lnmp2.0.tar.gz</span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#61AFEF;">tar</span><span style="color:#98C379;"> zxf</span><span style="color:#98C379;"> lnmp2.0.tar.gz</span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#56B6C2;">cd</span><span style="color:#98C379;"> lnmp2.0</span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#61AFEF;">./install.sh</span><span style="color:#98C379;"> lnmp</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h6 id="配置切换composer镜像源-先把默认的源禁用-再修改镜像源" tabindex="-1">配置切换Composer镜像源，先把默认的源禁用，再修改镜像源 <a class="header-anchor" href="#配置切换composer镜像源-先把默认的源禁用-再修改镜像源" aria-label="Permalink to &quot;配置切换Composer镜像源，先把默认的源禁用，再修改镜像源&quot;">​</a></h6><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 禁用默认源命令：</span></span>
<span class="line"><span style="color:#61AFEF;">composer</span><span style="color:#98C379;"> config</span><span style="color:#D19A66;"> -g</span><span style="color:#98C379;"> secure-http</span><span style="color:#D19A66;"> false</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 切换阿里云镜像源命令：</span></span>
<span class="line"><span style="color:#61AFEF;">composer</span><span style="color:#98C379;"> config</span><span style="color:#D19A66;"> -g</span><span style="color:#98C379;"> repo.packagist</span><span style="color:#98C379;"> composer</span><span style="color:#98C379;"> https://mirrors.aliyun.com/composer/</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">#查看配置</span></span>
<span class="line"><span style="color:#61AFEF;">composer</span><span style="color:#98C379;"> config</span><span style="color:#D19A66;"> -g</span><span style="color:#D19A66;"> -l</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p>按官网步骤安装好后，接下来就回暴露一些问题，</p><h6 id="composer-创建laravel会出现问题" tabindex="-1">Composer 创建laravel会出现问题 <a class="header-anchor" href="#composer-创建laravel会出现问题" aria-label="Permalink to &quot;Composer 创建laravel会出现问题&quot;">​</a></h6><img src="https://raw.gitcode.com/loveagri/pic/raw/main/2023-08-16/14/image-20230816144559612_20230816145630.png" alt="image-20230816144559612" style="zoom:50%;"><p>这个提示是需要修改配置文件php.ini，删掉需要的命令</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">vim</span><span style="color:#98C379;"> /usr/local/php/etc/php.ini</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p><img src="https://raw.gitcode.com/loveagri/pic/raw/main/2023-08-16/14/image-20230816145013398_20230816145635.png" alt="image-20230816145013398" loading="lazy"></p><h6 id="命令行创建主机" tabindex="-1">命令行创建主机 <a class="header-anchor" href="#命令行创建主机" aria-label="Permalink to &quot;命令行创建主机&quot;">​</a></h6><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">lnmp</span><span style="color:#98C379;"> vhost</span><span style="color:#98C379;"> add</span><span style="color:#7F848E;font-style:italic;"> #注意选择需要的模版，这里选择laravel,或者thinkphp</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>继续访问还会报错，这时就需要修改fastcgi.conf</p><p><img src="https://raw.gitcode.com/loveagri/pic/raw/main/2023-08-16/15/image-20230816150326090_20230816150327.png" alt="image-20230816150326090" loading="lazy"></p><p>之后将项目所有目录用户和用户组修改为www</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">chown</span><span style="color:#98C379;"> www.www</span><span style="color:#98C379;"> project/</span><span style="color:#D19A66;"> -R</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h6 id="一切完事之后重启nginx" tabindex="-1">一切完事之后重启nginx <a class="header-anchor" href="#一切完事之后重启nginx" aria-label="Permalink to &quot;一切完事之后重启nginx&quot;">​</a></h6><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">/usr/local/nginx/sbin/nginx</span><span style="color:#D19A66;"> -s</span><span style="color:#98C379;"> reload</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div>`,19)]))}const y=s(o,[["render",p]]);export{b as __pageData,y as default};
