import{_ as n,c as a,o as e,ah as l}from"./chunks/framework.BrJYEL96.js";const y=JSON.parse('{"title":"反向代理","description":"","frontmatter":{},"headers":[],"relativePath":"backend/nginx/reverse proxy.md","filePath":"backend/nginx/reverse proxy.md","lastUpdated":1743348162000}'),p={name:"backend/nginx/reverse proxy.md"};function o(r,s,t,c,i,b){return e(),a("div",null,s[0]||(s[0]=[l(`<h1 id="反向代理" tabindex="-1">反向代理 <a class="header-anchor" href="#反向代理" aria-label="Permalink to &quot;反向代理&quot;">​</a></h1><h2 id="upstream" tabindex="-1">upstream <a class="header-anchor" href="#upstream" aria-label="Permalink to &quot;upstream&quot;">​</a></h2><p>upstream模块用于定义上游服务的相关信息。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">upstream</span><span style="color:#98C379;"> name</span><span style="color:#98C379;"> {...}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 示例</span></span>
<span class="line"><span style="color:#61AFEF;">upstream</span><span style="color:#98C379;"> tomcat</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#56B6C2;">    ......</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><h3 id="指令" tabindex="-1">指令 <a class="header-anchor" href="#指令" aria-label="Permalink to &quot;指令&quot;">​</a></h3><table tabindex="0"><thead><tr><th>指令</th><th>含义</th></tr></thead><tbody><tr><td>upstream</td><td>段名，以{开始，}结束，中间定义上游服务的URL</td></tr><tr><td>server</td><td>定义上游服务地址</td></tr><tr><td>zone</td><td>定义共享内存，用于夸worker子进程</td></tr><tr><td>keepalive</td><td>对上游服务启动长连接</td></tr><tr><td>keepalive_requests</td><td>一个长连接最多请求个数</td></tr><tr><td>keepalive_timeout</td><td>空闲情形下，一个长连接的超时时长</td></tr><tr><td>hash</td><td>哈希负载均衡算法</td></tr><tr><td>ip_hash</td><td>依据IP进行哈希计算的负载均衡算法</td></tr><tr><td>least_conn</td><td>最少连接数负载均衡算法</td></tr><tr><td>least_time</td><td>最短响应时间负载均衡算法</td></tr><tr><td>random</td><td>随机负载均衡算法</td></tr></tbody></table><h3 id="upstream-1" tabindex="-1">upstream <a class="header-anchor" href="#upstream-1" aria-label="Permalink to &quot;upstream&quot;">​</a></h3><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">upstream</span><span style="color:#98C379;"> name</span><span style="color:#98C379;"> {...}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 示例</span></span>
<span class="line"><span style="color:#61AFEF;">upstream</span><span style="color:#98C379;"> tomcat</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#56B6C2;">    ......</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><h4 id="server" tabindex="-1">server <a class="header-anchor" href="#server" aria-label="Permalink to &quot;server&quot;">​</a></h4><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 upstream</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">server</span><span style="color:#98C379;"> address</span><span style="color:#ABB2BF;"> [parameters];</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h5 id="parameters" tabindex="-1">parameters <a class="header-anchor" href="#parameters" aria-label="Permalink to &quot;parameters&quot;">​</a></h5><table tabindex="0"><thead><tr><th>可选参数</th><th>含义</th></tr></thead><tbody><tr><td>weight=number</td><td>权重值，默认为1</td></tr><tr><td>max_conns=number</td><td>上游服务器的最大并发连接数</td></tr><tr><td>fail_timeout=time</td><td>服务器不可用的判定时间</td></tr><tr><td>max_fails=number</td><td>服务器不可用的检查次数</td></tr><tr><td>backup</td><td>备份服务器，仅当其它服务器都不可用时启用</td></tr><tr><td>down</td><td>标记服务器长期不可用，离线维护</td></tr></tbody></table><h4 id="keepalive" tabindex="-1">keepalive <a class="header-anchor" href="#keepalive" aria-label="Permalink to &quot;keepalive&quot;">​</a></h4><p>限制每个worker子进程与上游服务器空闲长连接的最大数量。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 upstream</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive</span><span style="color:#98C379;"> connnections</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">#示例</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive</span><span style="color:#98C379;"> 16；</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h5 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h5><h4 id="keepalive-requests" tabindex="-1">keepalive_requests <a class="header-anchor" href="#keepalive-requests" aria-label="Permalink to &quot;keepalive_requests&quot;">​</a></h4><p>单个长连接可以处理的最多HTTP请求个数。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 upstream</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_requests</span><span style="color:#98C379;"> number</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_requests</span><span style="color:#98C379;"> 100；</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h4 id="keepalive-timeout" tabindex="-1">keepalive_timeout <a class="header-anchor" href="#keepalive-timeout" aria-label="Permalink to &quot;keepalive_timeout&quot;">​</a></h4><p>空闲长连接的最长保持时间。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 upstream</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_timeout</span><span style="color:#98C379;"> time</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_timeout</span><span style="color:#98C379;"> 60s；</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h5 id="-1" tabindex="-1"><a class="header-anchor" href="#-1" aria-label="Permalink to &quot;&quot;">​</a></h5><h4 id="queue" tabindex="-1">queue <a class="header-anchor" href="#queue" aria-label="Permalink to &quot;queue&quot;">​</a></h4><p>商业Nginx的参数，表示所有上游服务器不可用时，请求会被放到队列中等待。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 upstream</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">queue</span><span style="color:#98C379;"> number</span><span style="color:#ABB2BF;"> [timeout=time];</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">#示例</span></span>
<span class="line"><span style="color:#61AFEF;">queue</span><span style="color:#D19A66;"> 100</span><span style="color:#98C379;"> timeout=30s；</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h5 id="-2" tabindex="-1"><a class="header-anchor" href="#-2" aria-label="Permalink to &quot;&quot;">​</a></h5><p><strong>示例</strong></p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">upstream</span><span style="color:#98C379;"> back_end</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#61AFEF;">	server</span><span style="color:#98C379;"> 127.0.0.1:8080</span><span style="color:#98C379;"> weight=</span><span style="color:#D19A66;">3</span><span style="color:#98C379;"> max_conns=</span><span style="color:#D19A66;">1000</span><span style="color:#98C379;"> fail_timeout=10s</span><span style="color:#98C379;"> max_fails=</span><span style="color:#D19A66;">2</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">	keepalive</span><span style="color:#D19A66;"> 32</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">	keepalive_requests</span><span style="color:#D19A66;"> 50</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">	keepalive_timeout</span><span style="color:#98C379;"> 30s</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">server</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#61AFEF;">	listen</span><span style="color:#D19A66;"> 80</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">	server_name</span><span style="color:#98C379;"> localhost</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#ABB2BF;">	</span></span>
<span class="line"><span style="color:#61AFEF;">	location</span><span style="color:#98C379;"> /proxy</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#61AFEF;">		proxy_pass</span><span style="color:#98C379;"> http://back_end/proxy</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#ABB2BF;">	}</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br></div></div><h2 id="proxy-pass" tabindex="-1">proxy_pass <a class="header-anchor" href="#proxy-pass" aria-label="Permalink to &quot;proxy_pass&quot;">​</a></h2><p>由http_proxy模块提供（ngx_http_proxy_module）</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 location if limit_except</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass</span><span style="color:#98C379;"> URL</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 示例</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># URL必须以http或https开头</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># URL中可以携带变量</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># URL中是否带URI，会直接影响发往上游的请求的URL</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass</span><span style="color:#98C379;"> http://127.0.0.1:8080</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass</span><span style="color:#98C379;"> http://127.0.0.1:8080/proxy</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br></div></div><div class="tip custom-block"><p class="custom-block-title">带<code>/</code>与不带<code>/</code>用法区别</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">proxy_pass</span><span style="color:#98C379;"> http://127.0.0.1:8080</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass</span><span style="color:#98C379;"> http://127.0.0.1:8080/</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 不带反斜线</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 用户请求URL：/bbs/abc/test.html</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 请求到达Nginx的URL：/bbs/abc/test.html</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 请求到达上游应用服务器的URL：/bbs/abc/test.html</span></span>
<span class="line"><span style="color:#61AFEF;">location</span><span style="color:#98C379;"> /bbs/</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#61AFEF;">	proxy_pass</span><span style="color:#98C379;"> http://127.0.0.1:8080</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 带反斜线</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 用户请求URL：/bbs/abc/test.html</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 请求到达Nginx的URL：/bbs/abc/test.html</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 请求到达上游应用服务器的URL：/abc/test.html</span></span>
<span class="line"><span style="color:#61AFEF;">location</span><span style="color:#98C379;"> /bbs/</span><span style="color:#98C379;"> {</span></span>
<span class="line"><span style="color:#61AFEF;">	proxy_pass</span><span style="color:#98C379;"> http://127.0.0.1:8080/</span></span>
<span class="line"><span style="color:#ABB2BF;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br></div></div><p><strong>总结</strong></p><p>不带<code>/</code>意味着Nginx不会修改用户URL，而是直接透传给上游的应用服务器</p><p>带<code>/</code>意味这Nginx会修改用户URL，修改方法：将location后的URL从用户URL中删除</p></div><h3 id="proxy-request-buffering" tabindex="-1">proxy_request_buffering <a class="header-anchor" href="#proxy-request-buffering" aria-label="Permalink to &quot;proxy_request_buffering&quot;">​</a></h3><p>如果开启，会开辟一片缓冲区，接受完客户端发来的数据后一次性传递给上游服务器。适应场景是</p><ol><li>吞吐量要求高</li><li>上游服务器并发处理能力低</li></ol><p>如果是off，那么适用的场景是，Nginx接受处理，和传到上游服务器性能较优的时候，这样可以更及时的响应，减少Nginx磁盘I/O。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_request_buffering</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_request_buffering</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h4 id="client-body-buffer-size" tabindex="-1">client_body_buffer_size <a class="header-anchor" href="#client-body-buffer-size" aria-label="Permalink to &quot;client_body_buffer_size&quot;">​</a></h4><p>缓冲区大小，一般会看请求头里的<code>Content-Length</code>大小，如果小于，则存在内存中；如果大于会存入磁盘，一般是<code>client_body_temp_pat</code>指定的目录。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_buffer_size</span><span style="color:#98C379;"> size</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 32位8k, 64位16k</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_buffer_size</span><span style="color:#98C379;"> 8k</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">16k</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h4 id="client-max-body-size" tabindex="-1">client_max_body_size <a class="header-anchor" href="#client-max-body-size" aria-label="Permalink to &quot;client_max_body_size&quot;">​</a></h4><p>决定Nginx可以处理请求体大小，一般会看请求头里的<code>Content-Length</code>大小，超过一般会返回<code>413</code>状态码。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">client_max_body_size</span><span style="color:#98C379;"> size</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 大于1M不处理，直接丢弃，尤其是上传文件的时候</span></span>
<span class="line"><span style="color:#61AFEF;">client_max_body_size</span><span style="color:#98C379;"> 1M</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h4 id="client-body-in-single-buffer" tabindex="-1">client_body_in_single_buffer <a class="header-anchor" href="#client-body-in-single-buffer" aria-label="Permalink to &quot;client_body_in_single_buffer&quot;">​</a></h4><p>请求体尽量分配到内存中连续的空间上。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_in_single_buffer</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_in_single_buffer</span><span style="color:#98C379;"> off</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h4 id="client-body-temp-path" tabindex="-1">client_body_temp_path <a class="header-anchor" href="#client-body-temp-path" aria-label="Permalink to &quot;client_body_temp_path&quot;">​</a></h4><p>请求体大于<code>client_body_buffer_size</code>后，存入磁盘的目录。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_temp_path</span><span style="color:#98C379;"> path</span><span style="color:#ABB2BF;"> [level1] [level2] [level3];</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 一般是相对于Nginx部署的Nginx根目录</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_temp_path</span><span style="color:#98C379;"> client_body_temp</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h4 id="client-body-in-file-only" tabindex="-1">client_body_in_file_only <a class="header-anchor" href="#client-body-in-file-only" aria-label="Permalink to &quot;client_body_in_file_only&quot;">​</a></h4><p>请求体直接存入磁盘的目录。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># clean 请求完成后会删掉，on则不会删除</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_in_file_only</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">clean</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 一般是相对于Nginx部署的Nginx根目录</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_in_file_only</span><span style="color:#98C379;"> off</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><h4 id="client-body-timeout" tabindex="-1">client_body_timeout <a class="header-anchor" href="#client-body-timeout" aria-label="Permalink to &quot;client_body_timeout&quot;">​</a></h4><p>客户端和Nginx建立连接后，在超时时间内没有发送请求的话，会断开连接。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># clean 请求完成后会删掉，on则不会删除</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_timeout</span><span style="color:#98C379;"> time</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">client_body_timeout</span><span style="color:#98C379;"> 60s</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h2 id="请求行修改指令" tabindex="-1">请求行修改指令 <a class="header-anchor" href="#请求行修改指令" aria-label="Permalink to &quot;请求行修改指令&quot;">​</a></h2><h3 id="proxy-method" tabindex="-1">proxy_method <a class="header-anchor" href="#proxy-method" aria-label="Permalink to &quot;proxy_method&quot;">​</a></h3><p>修改客户端的请求行方法，然后传给上游服务器。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_method</span><span style="color:#98C379;"> method</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="proxy-http-version" tabindex="-1">proxy_http_version <a class="header-anchor" href="#proxy-http-version" aria-label="Permalink to &quot;proxy_http_version&quot;">​</a></h3><p>修改客户端的请求行协议，然后传给上游服务器。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_http_version</span><span style="color:#D19A66;"> 1.0</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">1.1</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 1.0协议不支持keepalive，所以如果需要客户端与上游服务器保持长连接需要设置为1.1</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_http_version</span><span style="color:#D19A66;"> 1.0</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br></div></div><h3 id="proxy-set-header" tabindex="-1">proxy_set_header <a class="header-anchor" href="#proxy-set-header" aria-label="Permalink to &quot;proxy_set_header&quot;">​</a></h3><p>修改客户端的请求头，然后传给上游服务器。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_set_header</span><span style="color:#98C379;"> field</span><span style="color:#98C379;"> value</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 1.0协议不支持keepalive，所以如果需要客户端与上游服务器保持长连接需要设置为1.1，同时将Connection on</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_set_header</span><span style="color:#98C379;"> Host</span><span style="color:#E06C75;"> $proxy_host</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_set_header</span><span style="color:#98C379;"> Connection</span><span style="color:#98C379;"> close</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><h3 id="proxy-pass-request-header" tabindex="-1">proxy_pass_request_header <a class="header-anchor" href="#proxy-pass-request-header" aria-label="Permalink to &quot;proxy_pass_request_header&quot;">​</a></h3><p>如果是on，客户端的请求头会原封不动传给上游服务器，off则相反，且只会禁止客户端传来的header，Nginx自身通过<code>proxy_set_header</code>不起作用。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass_request_header</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass_request_header</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h3 id="proxy-set-body" tabindex="-1">proxy_set_body <a class="header-anchor" href="#proxy-set-body" aria-label="Permalink to &quot;proxy_set_body&quot;">​</a></h3><p>修改客户端的请求体内容。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_set_body</span><span style="color:#98C379;"> value</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br></div></div><h3 id="proxy-pass-request-body" tabindex="-1">proxy_pass_request_body <a class="header-anchor" href="#proxy-pass-request-body" aria-label="Permalink to &quot;proxy_pass_request_body&quot;">​</a></h3><p>如果是on，客户端的请求体会原封不动传给上游服务器，off则相反，且只会禁止客户端传来的body，Nginx自身通过<code>proxy_set_body</code>不起作用。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass_request_body</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_pass_request_body</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h2 id="长连接" tabindex="-1">长连接 <a class="header-anchor" href="#长连接" aria-label="Permalink to &quot;长连接&quot;">​</a></h2><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 客户端到Nginx，位于http段中，默认就有，即已经开启，如果设置为0则是关闭。</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_timeout</span><span style="color:#D19A66;"> 65</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># Nginx到上游服务器，位于upstream段中</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive</span><span style="color:#D19A66;"> 32</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_requests</span><span style="color:#D19A66;"> 100</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"><span style="color:#61AFEF;">keepalive_timeout</span><span style="color:#98C379;"> 60s</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h3 id="proxy-connect-timeout" tabindex="-1">proxy_connect_timeout <a class="header-anchor" href="#proxy-connect-timeout" aria-label="Permalink to &quot;proxy_connect_timeout&quot;">​</a></h3><p>Nginx连接上游服务器的超时时间，即三次握手后一定时间内没有和上游服务器连接成功，则断开。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_connect_timeout</span><span style="color:#98C379;"> time</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_connect_timeout</span><span style="color:#98C379;"> 60s</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h3 id="proxy-socket-keepalive" tabindex="-1">proxy_socket_keepalive <a class="header-anchor" href="#proxy-socket-keepalive" aria-label="Permalink to &quot;proxy_socket_keepalive&quot;">​</a></h3><p>打开TCP层长连接，TCP层长连接由内核提供。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_socket_keepalive</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_socket_keepalive</span><span style="color:#98C379;"> off</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h3 id="proxy-send-timeout" tabindex="-1">proxy_send_timeout <a class="header-anchor" href="#proxy-send-timeout" aria-label="Permalink to &quot;proxy_send_timeout&quot;">​</a></h3><p>Nginx在一定时间内没有向上游服务器发送内容，则断开。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_connect_timeout</span><span style="color:#98C379;"> time</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_connect_timeout</span><span style="color:#98C379;"> 60s</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div><h3 id="proxy-ignore-client-bort" tabindex="-1">proxy_ignore_client_bort <a class="header-anchor" href="#proxy-ignore-client-bort" aria-label="Permalink to &quot;proxy_ignore_client_bort&quot;">​</a></h3><p>代理服务器Nginx是否主动放弃客户端主动断开连接的请求，如果开启则忽略已经发送给上游服务器的请求。</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 上下文 http server location</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 语法</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_ignore_client_bort</span><span style="color:#98C379;"> on</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">off</span><span style="color:#ABB2BF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 默认值</span></span>
<span class="line"><span style="color:#61AFEF;">proxy_ignore_client_bort</span><span style="color:#98C379;"> off</span><span style="color:#ABB2BF;">;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br></div></div>`,89)]))}const u=n(p,[["render",o]]);export{y as __pageData,u as default};
