import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.BrJYEL96.js";const u=JSON.parse('{"title":"Linux","description":"","frontmatter":{"barName":"Linux"},"headers":[],"relativePath":"computer/Linux/index.md","filePath":"computer/Linux/index.md","lastUpdated":1754837914000}'),o={name:"computer/Linux/index.md"};function t(l,s,p,d,c,i){return n(),a("div",null,s[0]||(s[0]=[r('<h1 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h1><table tabindex="0"><thead><tr><th>系统</th><th>加速</th></tr></thead><tbody><tr><td><a href="https://github.com/torvalds/linux" target="_blank" rel="noreferrer">内核</a></td><td><a href="https://git.kernel.org/" target="_blank" rel="noreferrer">内核</a></td></tr><tr><td><a href="https://mirrors.cloud.tencent.com/repo/" target="_blank" rel="noreferrer">软件包腾讯仓库</a></td><td></td></tr><tr><td><a href="https://mobaxterm.mobatek.net/" target="_blank" rel="noreferrer">Mobaxterm</a></td><td>类似putty工具，只有Windows版</td></tr></tbody></table><p>将Linux公钥写入服务器以实现免密登录，你可以使用SSH命令进行操作。以下是步骤和示例代码：</p><ol><li>生成公钥和私钥对（如果你还没有的话）。</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ssh-keygen</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><ol><li>将公钥复制到服务器。你可以使用<code>ssh-copy-id</code>命令，它会自动将公钥添加到服务器的<code>~/.ssh/authorized_keys</code>文件中。</li></ol><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ssh-copy-id</span><span style="color:#98C379;"> user@server</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>在执行上述命令时，系统会提示你输入服务器用户的密码。输入密码后，你的公钥就会被写入服务器，以后你就可以使用SSH密钥而不是密码来登录服务器了。</p><p>如果<code>ssh-copy-id</code>不可用，你可以手动将公钥内容追加到服务器的<code>~/.ssh/authorized_keys</code>文件中：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">cat</span><span style="color:#98C379;"> ~/.ssh/id_rsa.pub</span><span style="color:#ABB2BF;"> | </span><span style="color:#61AFEF;">ssh</span><span style="color:#98C379;"> user@server</span><span style="color:#98C379;"> &#39;mkdir -p ~/.ssh &amp;&amp; cat &gt;&gt; ~/.ssh/authorized_keys&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>确保服务器的<code>~/.ssh</code>目录的权限和所有权设置正确，通常<code>~/.ssh</code>目录的权限应该是700，<code>~/.ssh/authorized_keys</code>文件的权限应该是600。可以使用以下命令来设置正确的权限：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ssh</span><span style="color:#98C379;"> user@server</span><span style="color:#98C379;"> &#39;chmod 700 ~/.ssh &amp;&amp; chmod 600 ~/.ssh/authorized_keys&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>现在，你应该能够无密码登录服务器：</p><div class="language-bash line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">ssh</span><span style="color:#98C379;"> user@server</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div>',14)]))}const h=e(o,[["render",t]]);export{u as __pageData,h as default};
