import{_ as s,c as a,o as n,ah as o}from"./chunks/framework.BrJYEL96.js";const b=JSON.parse('{"title":"selinux","description":"","frontmatter":{},"headers":[],"relativePath":"computer/Linux/selinux.md","filePath":"computer/Linux/selinux.md","lastUpdated":1754837914000}'),l={name:"computer/Linux/selinux.md"};function r(i,e,t,c,p,d){return n(),a("div",null,e[0]||(e[0]=[o('<h1 id="selinux" tabindex="-1">selinux <a class="header-anchor" href="#selinux" aria-label="Permalink to &quot;selinux&quot;">​</a></h1><table tabindex="0"><thead><tr><th>web</th><th>desc</th></tr></thead><tbody><tr><td><a href="https://baijiahao.baidu.com/s?id=1625352423414836320&amp;wfr=spider&amp;for=pc" target="_blank" rel="noreferrer">服务权限位</a></td><td></td></tr></tbody></table><p>SELinux（Security-Enhanced Linux）是一个针对Linux操作系统的安全模块，它提供了额外的访问控制安全策略。在许多Linux发行版中，SELinux是默认开启的，尤其是在企业级和安全要求较高的环境中。</p><div class="warning custom-block github-alert"><p class="custom-block-title">WARNING</p><p>Ubuntu 18.04及以后版本）默认不包含SELinux。</p></div><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li><p>使用<code>getenforce</code>命令查看状态</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">getenfore</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p>使用<code>setenforce</code>命令，设置状态</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">sudo</span><span style="color:#98C379;"> setenforce</span><span style="color:#D19A66;"> 0</span><span style="color:#7F848E;font-style:italic;"> # 0 off, 1 on</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p>修改SELinux配置文件</p><p>编辑<code>/etc/selinux/config</code>文件，将<code>SELINUX</code>设置为<code>disabled</code></p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#E06C75;">SELINUX</span><span style="color:#56B6C2;">=</span><span style="color:#98C379;">disabled</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p>永久开启SELinux</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#E06C75;">SELINUX</span><span style="color:#56B6C2;">=</span><span style="color:#98C379;">enforcing</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li><li><p>重启</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">reboot</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div></li></ol>',6)]))}const h=s(l,[["render",r]]);export{b as __pageData,h as default};
