import{_ as d,c as e,o as a,ah as r}from"./chunks/framework.BrJYEL96.js";const p=JSON.parse('{"title":"sed(Stream editor)","description":"","frontmatter":{},"headers":[],"relativePath":"computer/Linux/shell/sed.md","filePath":"computer/Linux/shell/sed.md","lastUpdated":1754837914000}'),o={name:"computer/Linux/shell/sed.md"};function n(i,t,h,l,s,u){return a(),e("div",null,t[0]||(t[0]=[r('<h1 id="sed-stream-editor" tabindex="-1">sed(Stream editor) <a class="header-anchor" href="#sed-stream-editor" aria-label="Permalink to &quot;sed(Stream editor)&quot;">​</a></h1><table tabindex="0"><thead><tr><th>web</th><th>des</th></tr></thead><tbody><tr><td><a href="https://blog.csdn.net/h4241778/article/details/125263518" target="_blank" rel="noreferrer">blog</a></td><td></td></tr></tbody></table><h2 id="工作模式" tabindex="-1">工作模式 <a class="header-anchor" href="#工作模式" aria-label="Permalink to &quot;工作模式&quot;">​</a></h2><p>第一种：<code>stdout | sed [option] &quot;pattern command&quot;</code></p><p>第二种：<code>sed [option] &quot;pattern command&quot; file</code></p><h2 id="选项" tabindex="-1">选项 <a class="header-anchor" href="#选项" aria-label="Permalink to &quot;选项&quot;">​</a></h2><table tabindex="0"><thead><tr><th>选项</th><th>描述</th></tr></thead><tbody><tr><td>-n</td><td>只打印模式匹配行，<span style="color:red;">不加<code>-n</code>参数则所有行都打印</span></td></tr><tr><td>-e</td><td>直接在命令行进行sed编辑，用于多个匹配，or关系，如：-e /patter1/ -e /patter2/</td></tr><tr><td>-f</td><td>编辑命令保存于文件，指定文件执行</td></tr><tr><td>-r</td><td>支持正则</td></tr><tr><td>-i</td><td>直接修改文件内容</td></tr></tbody></table><h2 id="匹配模式" tabindex="-1">匹配模式 <a class="header-anchor" href="#匹配模式" aria-label="Permalink to &quot;匹配模式&quot;">​</a></h2><table tabindex="0"><thead><tr><th>序号</th><th>模式</th><th>示例</th><th>描述</th></tr></thead><tbody><tr><td>1</td><td>LineNumber</td><td>sed -n &quot;17p&quot; file</td><td>指定行号</td></tr><tr><td>2</td><td>startLine, endLine</td><td>sed -n &quot;10, 20p&quot; file</td><td>起始到结束行</td></tr><tr><td>3</td><td>StartLine ,+n</td><td>sed -n &quot;10, +5p&quot; file</td><td>指定起始行号，然后后面n行</td></tr><tr><td>4</td><td>/pattern1/</td><td>sed -n &quot;/^root/p&quot; file</td><td>正则匹配行</td></tr><tr><td>5</td><td>/pattern1/,/pattern1/</td><td>sed -n &quot;/pattern1/,/pattern2/p&quot; file</td><td>从匹配的行到匹配的行</td></tr><tr><td>6</td><td>LineNumber,/pattern1/</td><td>sed -n &quot;10,/^hdfs/p&quot; file</td><td>从指定行号匹配到匹配行</td></tr><tr><td>7</td><td>/pattern1/,LineNumber</td><td>sed -n &quot;/^hdfs/,10p&quot; file</td><td>从匹配行匹配到指定行号</td></tr></tbody></table><h2 id="编辑命令" tabindex="-1">编辑命令 <a class="header-anchor" href="#编辑命令" aria-label="Permalink to &quot;编辑命令&quot;">​</a></h2><table tabindex="0"><thead><tr><th>动作</th><th>命令</th><th>示例</th><th>描述</th></tr></thead><tbody><tr><td>打印</td><td>p</td><td>sed -n &quot;/pattern/p&quot; file</td><td>打印匹配内容</td></tr><tr><td>删除</td><td>d</td><td>sed -i &quot;/pattern/d&quot; file</td><td>Delete match content</td></tr><tr><td>增加</td><td>a</td><td>sed -i &quot;/pattern/a content&quot; file</td><td>add after the match line</td></tr><tr><td>增加</td><td>i</td><td>sed -i &quot;/pattern/i content&quot; file</td><td>add before the match line</td></tr><tr><td>增加</td><td>r</td><td>sed -i &quot;/pattern/r file1&quot; file</td><td>Add the the content of file1 after the match line</td></tr><tr><td>增加</td><td>w</td><td>sed -i &quot;/pattern/w file1&quot; file</td><td>write the match line to the file1</td></tr></tbody></table><h2 id="修改命令" tabindex="-1">修改命令 <a class="header-anchor" href="#修改命令" aria-label="Permalink to &quot;修改命令&quot;">​</a></h2><p><code>s/pattern/string/ing</code> n表示替换第二个匹配字符串，ng表示从第二个开始替换所有匹配， 表示忽略大小写</p><h2 id="显示行号" tabindex="-1">显示行号 <a class="header-anchor" href="#显示行号" aria-label="Permalink to &quot;显示行号&quot;">​</a></h2><p><code>sed -n &quot;/pattern/=&quot; file</code></p><h2 id="删除命令" tabindex="-1">删除命令 <a class="header-anchor" href="#删除命令" aria-label="Permalink to &quot;删除命令&quot;">​</a></h2><p><code>sed -i &quot;/[:blank:]*#/d&quot; file</code> 删除以<code>#</code>开头但是<code>#</code>前边有空格的注释行</p>',17)]))}const q=d(o,[["render",n]]);export{p as __pageData,q as default};
