import{_ as n,c as a,o as l,ah as p}from"./chunks/framework.BrJYEL96.js";const u=JSON.parse('{"title":"git 基本命令","description":"","frontmatter":{"order":1},"headers":[],"relativePath":"tools/git/git-basic-command.md","filePath":"tools/git/git-basic-command.md","lastUpdated":1749526188000}'),e={name:"tools/git/git-basic-command.md"};function o(r,s,c,t,i,b){return l(),a("div",null,s[0]||(s[0]=[p(`<h1 id="git-基本命令" tabindex="-1">git 基本命令 <a class="header-anchor" href="#git-基本命令" aria-label="Permalink to &quot;git 基本命令&quot;">​</a></h1><h2 id="克隆" tabindex="-1"><strong>克隆</strong> <a class="header-anchor" href="#克隆" aria-label="Permalink to &quot;**克隆**&quot;">​</a></h2><h3 id="git-clone" tabindex="-1">git clone <a class="header-anchor" href="#git-clone" aria-label="Permalink to &quot;git clone&quot;">​</a></h3><p>克隆仓库时希望本地分支名不同于远程默认的 <code>main</code>/<code>master</code>，可以使用：</p><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clone</span><span style="color:#D19A66;"> -b</span><span style="color:#98C379;"> 远程分支名</span><span style="color:#D19A66;"> --origin</span><span style="color:#98C379;"> 远程名</span><span style="color:#98C379;"> 仓库URL</span><span style="color:#98C379;"> 本地目录名</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 克隆特定分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clone</span><span style="color:#D19A66;"> -b</span><span style="color:#98C379;"> develop</span><span style="color:#98C379;"> git@github.com:user/repo.git</span><span style="color:#98C379;"> myrepo</span></span>
<span class="line"><span style="color:#56B6C2;">cd</span><span style="color:#98C379;"> myrepo</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -m</span><span style="color:#98C379;"> develop</span><span style="color:#98C379;"> dev</span><span style="color:#7F848E;font-style:italic;">       # 重命名本地分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -u</span><span style="color:#98C379;"> origin/develop</span><span style="color:#7F848E;font-style:italic;">   # 重新关联远程</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 克隆时指定深度（浅克隆）</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 只克隆最近一次提交（节省时间/空间）：</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clone</span><span style="color:#D19A66;"> --depth</span><span style="color:#D19A66;"> 1</span><span style="color:#98C379;"> https://github.com/user/repo.git</span><span style="color:#7F848E;font-style:italic;"> # --depth 1 表示只克隆最新版本，不包含完整历史。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 克隆裸仓库（Bare Repository）</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clone</span><span style="color:#D19A66;"> --bare</span><span style="color:#98C379;"> https://github.com/user/repo.git</span></span>
<span class="line"></span>
<span class="line"><span style="color:#61AFEF;">$</span><span style="color:#98C379;"> ls</span></span>
<span class="line"><span style="color:#61AFEF;">config</span><span style="color:#98C379;">      description</span><span style="color:#98C379;"> HEAD</span><span style="color:#98C379;">        hooks</span><span style="color:#98C379;">       info</span><span style="color:#98C379;">        objects</span><span style="color:#98C379;">     packed-refs</span><span style="color:#98C379;"> refs</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br></div></div><h2 id="工作区管理" tabindex="-1">工作区管理 <a class="header-anchor" href="#工作区管理" aria-label="Permalink to &quot;工作区管理&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 命令用来从工作目录中删除所有没有跟踪（tracked）过的文件</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clean</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 是一次clean的演习, 告诉你哪些文件会被删除</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clean</span><span style="color:#D19A66;"> -n</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 删除当前目录下没有tracked过的文件，不会删除.gitignore指定的文件</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clean</span><span style="color:#D19A66;"> -f</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 删除当前目录下没有被tracked过的文件和文件夹</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> clean</span><span style="color:#D19A66;"> -df</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 将没有放入到暂存区的所有文件恢复</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> .</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;">#放弃指定文件的修改</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> file</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 暂存现在工作区的工作，然后恢复原来为编辑的状态，但是不包括未被跟踪的文件，也就是新建的文件</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> stash</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 显示现在正在暂存的工作</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> stash</span><span style="color:#98C379;"> list</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 恢复stash暂存区的工作</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> stash</span><span style="color:#98C379;"> apply</span><span style="color:#98C379;"> stash@{</span><span style="color:#D19A66;">0</span><span style="color:#98C379;">}</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br></div></div><h2 id="暂存区管理" tabindex="-1">暂存区管理 <a class="header-anchor" href="#暂存区管理" aria-label="Permalink to &quot;暂存区管理&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 提交所有修改和新增的文件</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> add</span><span style="color:#98C379;"> .</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> add</span><span style="color:#D19A66;"> -u</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 撤销暂存区的所有文件</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#98C379;"> HEAD</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 撤销暂存区的指定文件</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#98C379;"> HEAD</span><span style="color:#98C379;"> file</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看暂存区文件列表</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> ls-files</span><span style="color:#D19A66;"> -s</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看暂存区文件内容</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> cat-file</span><span style="color:#D19A66;"> -p</span><span style="color:#98C379;"> 6e9a94</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 提交一次版本</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> commit</span><span style="color:#D19A66;"> -m</span><span style="color:#98C379;"> ‘提交信息’</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 提交一次版本， 同git add . &amp;&amp; git commit -m &quot;message&quot;</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> commit</span><span style="color:#D19A66;"> -am</span><span style="color:#98C379;"> ‘提交信息’</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 将误删除文件从上次版本库中恢复</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> HEAD</span><span style="color:#D19A66;"> --</span><span style="color:#98C379;"> deletedFile</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 将误删除文件从上上次提交的版本库中恢复</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> HEAD^</span><span style="color:#D19A66;"> --</span><span style="color:#98C379;"> deletedFile</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br></div></div><h2 id="版本库管理" tabindex="-1">版本库管理 <a class="header-anchor" href="#版本库管理" aria-label="Permalink to &quot;版本库管理&quot;">​</a></h2><h3 id="git-reset" tabindex="-1">git reset <a class="header-anchor" href="#git-reset" aria-label="Permalink to &quot;git reset&quot;">​</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;">#使用reset恢复到历史提交点，重置暂存区与工作目录的内容。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 保留工作区的内容，不保留暂存区 等同 git reset --mixed</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 保留工作区的内容，把文件差异放进暂存区，即保留暂存区</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#D19A66;"> --soft</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 清空工作区和暂存区的改动</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#D19A66;"> --hard</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 恢复前三个版本</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#D19A66;"> --hard</span><span style="color:#98C379;"> HEAD^^^</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># (同上)恢复前三个版本</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#D19A66;"> --hard</span><span style="color:#98C379;"> HEAD~3</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 恢复到指定提交版本（先通过 git log 查看版本号)</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#D19A66;"> --hard</span><span style="color:#98C379;"> b7b73147ca8d6fc20e451d7b36</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 放弃已经add 暂存区的文件hd.js</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reset</span><span style="color:#98C379;"> HEAD</span><span style="color:#98C379;"> hd.js</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 可以查询之前进行的 每一次 git 命令，即包括reset前的所有日志，即显示每一步操作信息</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> reflog</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br></div></div><h2 id="分支管理" tabindex="-1">分支管理 <a class="header-anchor" href="#分支管理" aria-label="Permalink to &quot;分支管理&quot;">​</a></h2><h3 id="git-branch" tabindex="-1">git branch <a class="header-anchor" href="#git-branch" aria-label="Permalink to &quot;git branch&quot;">​</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;">#分支用于为项目增加新功能或修复Bug时使用。</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 创建分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#98C379;"> dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 切换分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 创建并切换分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#D19A66;"> -b</span><span style="color:#98C379;"> feature/bbs</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -r</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 将分支 main 更新为master</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -m</span><span style="color:#98C379;"> main</span><span style="color:#98C379;"> master</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 合并dev分支到master</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> master</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> merge</span><span style="color:#98C379;"> dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 删除分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -d</span><span style="color:#98C379;"> dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 删除没有合并的分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -D</span><span style="color:#98C379;"> dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 删除远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#98C379;"> origin</span><span style="color:#98C379;"> :dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看未合并的分支(切换到master)</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> --no-merged</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看已经合并的分支(切换到master)</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> --merged</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看两个分支的变动</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> diff</span><span style="color:#98C379;"> branch-a..branch-b</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 修改现有分支的跟踪关系</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -u</span><span style="color:#98C379;"> origin/远程分支名</span><span style="color:#ABB2BF;"> [本地分支名]</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -u</span><span style="color:#98C379;"> origin/feature/dev</span><span style="color:#98C379;"> dev</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 如果当前就在 dev 分支上，可以省略本地分支名：</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> checkout</span><span style="color:#98C379;"> dev</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -u</span><span style="color:#98C379;"> origin/feature/dev</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 使用 git branch 删除远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -d</span><span style="color:#D19A66;"> -r</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">远程名</span><span style="color:#ABB2BF;">称&gt;</span><span style="color:#98C379;">/</span><span style="color:#ABB2BF;">&lt;</span><span style="color:#98C379;">分支</span><span style="color:#ABB2BF;">名&gt;  </span><span style="color:#7F848E;font-style:italic;"># 查看远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">远程名</span><span style="color:#ABB2BF;">称&gt; </span><span style="color:#D19A66;">--delete</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">分支</span><span style="color:#ABB2BF;">名&gt;  </span><span style="color:#7F848E;font-style:italic;"># 实际删除</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 示例</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -d</span><span style="color:#D19A66;"> -r</span><span style="color:#98C379;"> origin/feature/test</span><span style="color:#7F848E;font-style:italic;">  # 先解除关联（可选）</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#98C379;"> origin</span><span style="color:#D19A66;"> --delete</span><span style="color:#98C379;"> feature/test</span><span style="color:#7F848E;font-style:italic;"> # 再删除远程分支</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br><span class="line-number">33</span><br><span class="line-number">34</span><br><span class="line-number">35</span><br><span class="line-number">36</span><br><span class="line-number">37</span><br><span class="line-number">38</span><br><span class="line-number">39</span><br><span class="line-number">40</span><br><span class="line-number">41</span><br><span class="line-number">42</span><br><span class="line-number">43</span><br><span class="line-number">44</span><br><span class="line-number">45</span><br><span class="line-number">46</span><br><span class="line-number">47</span><br><span class="line-number">48</span><br><span class="line-number">49</span><br><span class="line-number">50</span><br><span class="line-number">51</span><br><span class="line-number">52</span><br><span class="line-number">53</span><br><span class="line-number">54</span><br><span class="line-number">55</span><br></div></div><h2 id="日志查看" tabindex="-1">日志查看 <a class="header-anchor" href="#日志查看" aria-label="Permalink to &quot;日志查看&quot;">​</a></h2><h3 id="git-log" tabindex="-1">git log <a class="header-anchor" href="#git-log" aria-label="Permalink to &quot;git log&quot;">​</a></h3><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;">#查看日志</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看最近2次提交日志并显示文件差异</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> -p</span><span style="color:#D19A66;"> -2</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 显示已修改的文件清单</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --name-only</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 显示新增、修改、删除的文件清单</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --name-status</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 一行显示并只显示SHA-1的前几个字符</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --oneline</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 一行显示并只显示SHA-1的前几个字符及最近的5条信息</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --oneline</span><span style="color:#D19A66;"> -5</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 过滤日志信息</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --oneline</span><span style="color:#D19A66;"> --grep=</span><span style="color:#98C379;">&quot;filer info&quot;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看特定时间段日志</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --before=</span><span style="color:#98C379;">&#39;1 day&#39;</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> log</span><span style="color:#D19A66;"> --after=</span><span style="color:#98C379;">&#39;1 day&#39;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br></div></div><h2 id="远程管理" tabindex="-1">远程管理 <a class="header-anchor" href="#远程管理" aria-label="Permalink to &quot;远程管理&quot;">​</a></h2><h3 id="git-remote" tabindex="-1">git remote <a class="header-anchor" href="#git-remote" aria-label="Permalink to &quot;git remote&quot;">​</a></h3><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 添加远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> remote</span><span style="color:#98C379;"> add</span><span style="color:#98C379;"> origin</span><span style="color:#98C379;"> https://gitee.com/loveagri/blank.git</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> branch</span><span style="color:#D19A66;"> -r</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 查看远程分支关联地址</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># origin  git@gitee.com:loveagri/blank.git (fetch)</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># origin  git@gitee.com:loveagri/blank.git (push)</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> remote</span><span style="color:#D19A66;"> -v</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br></div></div><h2 id="推送分支到远程" tabindex="-1">推送分支到远程 <a class="header-anchor" href="#推送分支到远程" aria-label="Permalink to &quot;推送分支到远程&quot;">​</a></h2><h3 id="git-push" tabindex="-1">git push <a class="header-anchor" href="#git-push" aria-label="Permalink to &quot;git push&quot;">​</a></h3><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># git </span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#D19A66;"> -u</span><span style="color:#98C379;"> origin</span><span style="color:#98C379;"> 本地分支名:远程分支名</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 单次命令指定密钥</span></span>
<span class="line"><span style="color:#E06C75;">GIT_SSH_COMMAND</span><span style="color:#56B6C2;">=</span><span style="color:#98C379;">&quot;ssh -i ~/.ssh/your_private_key&quot;</span><span style="color:#61AFEF;"> git</span><span style="color:#98C379;"> push</span><span style="color:#98C379;"> origin</span><span style="color:#98C379;"> main</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p>为不同仓库使用不同密钥，在 <code>~/.ssh/config</code> 中配置多个 <code>Host</code> 块</p><div class="language-ini line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># GitLab 仓库使用密钥 ~/.ssh/gitlab_key</span></span>
<span class="line"><span style="color:#98C379;">Host gitlab.com</span></span>
<span class="line"><span style="color:#98C379;">  HostName gitlab.com</span></span>
<span class="line"><span style="color:#98C379;">  User git</span></span>
<span class="line"><span style="color:#98C379;">  IdentityFile ~/.ssh/gitlab_key</span></span>
<span class="line"><span style="color:#98C379;">  IdentitiesOnly yes  </span><span style="color:#7F848E;font-style:italic;"># 强制只使用此密钥</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># GitHub 仓库使用密钥 ~/.ssh/github_key</span></span>
<span class="line"><span style="color:#98C379;">Host github.com</span></span>
<span class="line"><span style="color:#98C379;">  HostName github.com</span></span>
<span class="line"><span style="color:#98C379;">  User git</span></span>
<span class="line"><span style="color:#98C379;">  IdentityFile ~/.ssh/github_key</span></span>
<span class="line"><span style="color:#98C379;">  IdentitiesOnly yes</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br></div></div><h4 id="关键点" tabindex="-1">关键点： <a class="header-anchor" href="#关键点" aria-label="Permalink to &quot;关键点：&quot;">​</a></h4><ul><li><p><strong><code>Host</code> 必须匹配 Git 远程地址的域名</strong>（如 <code>git@gitlab.com:user/repo.git</code> 对应 <code>Host gitlab.com</code>）。</p></li><li><p><strong><code>IdentitiesOnly yes</code></strong> 确保 SSH 不会尝试其他密钥。</p></li></ul><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 使用 git push 删除远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">远程名</span><span style="color:#ABB2BF;">称&gt; </span><span style="color:#D19A66;">--delete</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">分支</span><span style="color:#ABB2BF;">名&gt;</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 简写</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#ABB2BF;"> &lt;</span><span style="color:#98C379;">远程名</span><span style="color:#ABB2BF;">称&gt; </span><span style="color:#98C379;">:</span><span style="color:#ABB2BF;">&lt;</span><span style="color:#98C379;">分支</span><span style="color:#ABB2BF;">名&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 示例</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#98C379;"> origin</span><span style="color:#D19A66;"> --delete</span><span style="color:#98C379;"> feature/test</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 或</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> push</span><span style="color:#98C379;"> origin</span><span style="color:#98C379;"> :feature/test</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br></div></div><h2 id="远程拉取" tabindex="-1">远程拉取 <a class="header-anchor" href="#远程拉取" aria-label="Permalink to &quot;远程拉取&quot;">​</a></h2><h3 id="git-pull" tabindex="-1">git pull <a class="header-anchor" href="#git-pull" aria-label="Permalink to &quot;git pull&quot;">​</a></h3><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;">#直接拉取远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> pull</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="git-fetch" tabindex="-1">git fetch <a class="header-anchor" href="#git-fetch" aria-label="Permalink to &quot;git fetch&quot;">​</a></h3><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 拉取远程分支但是不合并</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> fetch</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h3 id="git-merge" tabindex="-1">git merge <a class="header-anchor" href="#git-merge" aria-label="Permalink to &quot;git merge&quot;">​</a></h3><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 合并远程分支</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> merge</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="git-diff" tabindex="-1">git diff <a class="header-anchor" href="#git-diff" aria-label="Permalink to &quot;git diff&quot;">​</a></h2><div class="language-sh line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki one-dark-pro vp-code" style="background-color:#282c34;color:#abb2bf;" tabindex="0"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 查看两个分支的变动</span></span>
<span class="line"><span style="color:#61AFEF;">git</span><span style="color:#98C379;"> diff</span><span style="color:#98C379;"> branch-a..branch-b</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,38)]))}const d=n(e,[["render",o]]);export{u as __pageData,d as default};
